mapper.select:
select
  id, user_id, role_id, CREATED_BY, CREATED_TIME, UPDATED_BY, UPDATED_TIME
from auth_user_role


        and id = #{id}


        and user_id = #{userId}


        and role_id = #{roleId}


        and CREATED_BY = #{createdBy}


        and CREATED_TIME = #{createdTime}


        and UPDATED_BY = #{updatedBy}


        and UPDATED_TIME = #{updatedTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from auth_user_role


        and id = #{id}


        and user_id = #{userId}


        and role_id = #{roleId}


        and CREATED_BY = #{createdBy}


        and CREATED_TIME = #{createdTime}


        and UPDATED_BY = #{updatedBy}


        and UPDATED_TIME = #{updatedTime}
