mapper.select:
select
  id, CREATED_BY, CREATED_TIME, UPDATED_BY, UPDATED_TIME, label_name, category_id, label_sort
from subject_label


        and id = #{id}


        and CREATED_BY = #{createdBy}


        and CREATED_TIME = #{createdTime}


        and UPDATED_BY = #{updatedBy}


        and UPDATED_TIME = #{updatedTime}


        and label_name = #{labelName}


        and category_id = #{categoryId}


        and label_sort = #{labelSort}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from subject_label


        and id = #{id}


        and CREATED_BY = #{createdBy}


        and CREATED_TIME = #{createdTime}


        and UPDATED_BY = #{updatedBy}


        and UPDATED_TIME = #{updatedTime}


        and label_name = #{labelName}


        and category_id = #{categoryId}


        and label_sort = #{labelSort}

mapper.delete:
DELETE FROM subject_label
WHERE id IN

    #{id}
