mapper.select:
select
  id, CREATED_BY, CREATED_TIME, UPDATED_BY, UPDATED_TIME, subject_id, option_type, option_content, is_correct
from subject_multiple


        and id = #{id}


        and CREATED_BY = #{createdBy}


        and CREATED_TIME = #{createdTime}


        and UPDATED_BY = #{updatedBy}


        and UPDATED_TIME = #{updatedTime}


        and subject_id = #{subjectId}


        and option_type = #{optionType}


        and option_content = #{optionContent}


        and is_correct = #{isCorrect}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from subject_multiple


        and id = #{id}


        and CREATED_BY = #{createdBy}


        and CREATED_TIME = #{createdTime}


        and UPDATED_BY = #{updatedBy}


        and UPDATED_TIME = #{updatedTime}


        and subject_id = #{subjectId}


        and option_type = #{optionType}


        and option_content = #{optionContent}


        and is_correct = #{isCorrect}
